import React from 'react';
import { storiesOf } from '@storybook/react';

import Card from "./Card"

storiesOf('Card', module)

  .add('mostrar año', () => {
    const card = { name: "El descubrimiento del fuego",  year: "-2000000", image: "data:image/jpeg;base64,"}
    return <Card card={card} showingInfo={true}/>
  })

  .add('carta con text muy largo', () => {
    const card = {name: "El descubrimiento de la relación entre los cromosomas y herencia",  year: "-2000000", image: "data:image/jpeg;base64,"}
    return <Card card={card} showingInfo={true} />
  })

  .add('no mostrar año', () => {
    const card = { name: "Fin de la segunda guerra mundial",  year: "1812", image: "data:image/jpeg;base64,"}
    return <Card card={card} showingInfo={false} />
  })


  .add('no mostrar año, seleccionable', () => {
    const card = { name: "Creación de la bandera",  year: "1812", image: "data:image/jpeg;base64,"}
    return <Card card={card} showingInfo={false} seleccionable={true} />
  })

  .add('carta seleccionada', () => {
    const card = { name: "Creación de la bandera",  year: "1812", image: "data:image/jpeg;base64,"}
    return <Card card={card} showingInfo={false} seleccionada={true} />
  })

 